import{parseGeneral}from"../general.js";import{getGotOptions,getResponse}from"../utils/got.js";import*as cheerio from"cheerio";export function test(url){return url.hostname==="bsky.app"}export async function summarize(url,opts){const args=getGotOptions(url.href,opts);const res=await getResponse({...args,method:"GET"});const body=res.body;const $=cheerio.load(body);return await parseGeneral(url,{body,$,response:res})}